import React from "react";
import StoreButton from "./StoreButton";




class Base extends React.Component{
    constructor() {
        super();
        this.state={
            out:"0"
        }

this.refOutput=React.createRef()

    }

    tapeNumber(value){
        let curentValue=value
        let output=this.refOutput.current
        this.setState({out:curentValue})
        if (output.value==="0"){output.value=''}
        output.value+=curentValue
    }

    tapeOperation(){
        let output=this.refOutput.current
        if (value ==='CE'){
            output.value=output.value.substring(0,output.value.length-1)
        }
    }
    render() {


        return (
            <div>
                <div>
                    <input ref={this.refOutput} type="text" defaultValue={this.state.out}/>
                </div>
                <div>
                    {StoreButton.buttons.map(item => <button
                        onClick={() => {
                            this.tapeNumber(item.num)
                        }}

                    >{item.num}</button>)}
                    {StoreButton.operation.map(item => <button
                        onClick={() => {
                            this.tapeOperation(item.num)
                        }}

                    >{item.num}</button>)}

                </div>
            </div>
        )
    }
}

export default Base;
